import { useState } from "react";
import styled from "styled-components";

const ChatInputContainer = styled.div`
width: 85%;
height: 30px;
display: flex;
align-items: center;
justify-content: space-between;
margin-top: 30px;
`
const Input = styled.input`
width: 85%;
height: 100%;
display: flex;
`
const Button = styled.button`
width: 13%;
height: 80%;
background-color: black;
display: flex;
align-items: center;
`

export const ChatInput = ({sendRequest})=>{
    const [inputs,setInputs] = useState("how are you?");

    return(
        <ChatInputContainer>
            <Input />
            <Button onClick={sendRequest(inputs)}/>
        </ChatInputContainer>
    )
}

export default ChatInput;
