import axios from "axios";
import { useState } from "react";
import ChatContent from "./components/ChatContent.jsx/ChatContent";
import styled from "styled-components";


const ChatBoxContainer = styled.div`
width: 740px;
height: 600px;
background-color: blue;
display: flex;
flex-direction: column;
justify-content: center;
align-items: center;
`


export const ChatBox =()=>{
    const [content,setContent] = useState("")

    const mockResponse = [
        {response: 'hello'}
    ]

    const sendRequest = ()=>{
      const API_KEY = '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';
      const API_URL = 'https://api-inference.huggingface.co/models/EleutherAI/gpt-neo-1.3B';
      const headers = { 'Authorization': `${API_KEY}`,'Content-Type': 'application/json'}
      const data = { 
        "inputs": "Hello, how are you?",
        "options": {
        "model": "EleutherAI/gpt-neo-1.3B",
        "temperature": 0.5,
        "max_length": 50
    } };

      axios.post(API_URL, data, { headers:{ 'Authorization': `Bearer ${API_KEY}`,'Content-Type': 'application/json'} })
      .then(response => {console.log(response.data[0].generated_text);setContent(response.data[0].generated_text)})
      .catch(error => {console.log(error); setContent(error.message)});
    }

    return(
        <ChatBoxContainer>
            <ChatContent/>
            <button onClick={sendRequest}></button>
            <div>{content}</div>
        </ChatBoxContainer>
    )
}

export default ChatBox;
